INSERT INTO public.place (id, name, descr) VALUES (1, 'Токио', 'Столица Японии, её административный, финансовый, промышленный и политический центр. Крупнейшая городская экономика мира. Расположен в юго-восточной части острова Хонсю, на равнине Канто в бухте Токийского залива Тихого океана. Помимо столицы, Токио также является одной из сорока семи префектур страны');
INSERT INTO public.place (id, name, descr) VALUES (2, 'Осака', 'Третий по населению город Японии, который находится в южной части острова Хонсю, близ устья реки Йодо в заливе Осака. Центр одноименной префектуры.');
INSERT INTO public.place (id, name, descr) VALUES (3, 'Киото', 'Расположен в центральной части острова Хонсю, в центре региона Кансай, в юго-западной части префектуры Киото. Город является административным центром этой префектуры. Один из ведущих городов региона Кансай и городского района Осака — Кобе — Киото.');
INSERT INTO public.place (id, name, descr) VALUES (5, 'Камакура', 'один из древних городов Японии, был основан в 1192 году. Расположен в префектуре Канагава, на острове Хонсю. Город с трёх сторон окружен лесистыми горами, а с юга выходит на залив Сагами. Климат Камакуры мягок. Это идеальный зимний и летний курорт.');
INSERT INTO public.place (id, name, descr) VALUES (6, 'Йокогама', 'Йокогама расположена в самом сердце Японии на побережье Тихого океана. Это второй по численности населения город страны с 3,7 миллионами жителей. Он располагается всего в получасе езды от Токио и является столицей префектуры Канагава. Йокогама – это один из 15 городов страны, спроектированный японским правительством.');
INSERT INTO public.place (id, name, descr) VALUES (7, 'Нагоя', 'Четвертый по численности населения город Японии (более 2-х миллионов человек) носит название Нагоя, что означает «именной старинный дом». Он является столицей префектуры Айти и главным городом региона Тюбу, а также равнины Ноби, одной из крупнейших равнин на острове Хонсю. Именно эти места считаются промышленным центром региона. Большая часть населения Нагои работает в традиционных для Тюбу индустриях: авиационная промышленность, керамика, текстиль, машиностроение и некоторые другие.');
INSERT INTO public.place (id, name, descr) VALUES (8, 'Наха', 'Наха является столицей и самым большим городом префектуры Окинава, которая располагается в южной части одноименного острова. На данный момент в Нахе проживает более 300 тысяч человек. Город считается важным транспортным центром региона: здесь есть и морской порт, и аэропорт, которые соединяют Наху с другими частями Окинавы, Японии и со всем остальным миром. До Токио из Нахи можно добраться всего за 2,5 часа, а до Осаки - за 2.');
